(165, '_site_transient_community-events-d41d8cd98f00b204e9800998ecf8427e', 'a:4:{s:9:\"sandboxed\";b:0;s:5:\"error\";N;s:8:\"location\";a:1:{s:2:\"ip\";b:0;}s:6:\"events\";a:6:{i:0;a:10:{s:4:\"type\";s:8:\"wordcamp\";s:5:\"title\";s:27:\"WordCamp Italia Online 2021\";s:3:\"url\";s:33:\"https://italia.wordcamp.org/2021/\";s:6:\"meetup\";N;s:10:\"meetup_url\";N;s:4:\"date\";s:19:\"2021-10-22 14:00:00\";s:8:\"end_date\";s:19:\"2021-10-23 00:00:00\";s:20:\"start_unix_timestamp\";i:1634904000;s:18:\"end_unix_timestamp\";i:1634940000;s:8:\"location\";a:4:{s:8:\"location\";s:6:\"Online\";s:7:\"country\";s:2:\"IT\";s:8:\"latitude\";d:41.872555300000002;s:9:\"longitude\";d:12.516448;}}i:1;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:25:\"WordPress Helsinki Meetup\";s:3:\"url\";s:55:\"https://www.meetup.com/WPHelsinki/events/nbnnjlydccbhb/\";s:6:\"meetup\";s:18:\"WordPress Helsinki\";s:10:\"meetup_url\";s:34:\"https://www.meetup.com/WPHelsinki/\";s:4:\"date\";s:19:\"2022-01-05 17:30:00\";s:8:\"end_date\";s:19:\"2022-01-05 19:00:00\";s:20:\"start_unix_timestamp\";i:1641396600;s:18:\"end_unix_timestamp\";i:1641402000;s:8:\"location\";a:4:{s:8:\"location\";s:12:\"HKI, Finland\";s:7:\"country\";s:2:\"FI\";s:8:\"latitude\";d:60.169998168945;s:9:\"longitude\";d:24.940000534058001;}}i:2;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:25:\"WordPress Helsinki Meetup\";s:3:\"url\";s:55:\"https://www.meetup.com/WPHelsinki/events/nbnnjlydcdbdb/\";s:6:\"meetup\";s:18:\"WordPress Helsinki\";s:10:\"meetup_url\";s:34:\"https://www.meetup.com/WPHelsinki/\";s:4:\"date\";s:19:\"2022-02-02 17:30:00\";s:8:\"end_date\";s:19:\"2022-02-02 19:00:00\";s:20:\"start_unix_timestamp\";i:1643815800;s:18:\"end_unix_timestamp\";i:1643821200;s:8:\"location\";a:4:{s:8:\"location\";s:12:\"HKI, Finland\";s:7:\"country\";s:2:\"FI\";s:8:\"latitude\";d:60.169998168945;s:9:\"longitude\";d:24.940000534058001;}}i:3;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:25:\"WordPress Helsinki Meetup\";s:3:\"url\";s: